STYLE = "* {font-family: 'Poppins';letter-spacing: 0.6px;}#menu,#exportMenu {border: 1px solid #AEAEAE;border-radius: 2px;icon-size: 22px;}#menu::icon,#exportMenu::icon {padding-left: 16px;}#menu::item,#exportMenu::item {height: 34px;background-color: transparent;color: #fff;padding: 0px 50px 0px 10px;font-size: 13px;font-weight: 700;}#menu::item:selected,#exportMenu::item:selected {background-color: #4E4E4E;}#menu,#exportMenu,#playerWidget,#headerWidget {background-color: #333333;}#headerWidget {border-top-left-radius: 5px;border-top-right-radius: 5px;}#headingLabel {color: #fff;font-size: 12px;font-weight: 700;margin-left: 33px;}#minimizeBtn,#maximizeBtn,#exitBtn {border: 0;border-radius: 10px;}#minimizeBtn {background-color: #24FF00;}#maximizeBtn {background-color: #FFE500;}#exitBtn {background-color: #FF3C3C;}#minimizeBtn:hover {background-color: #76FF60;}#maximizeBtn:hover {background-color: #FFEF5A;}#exitBtn:hover {background-color: #FF5757;}#playerWidget {border-bottom-left-radius: 5px;border-bottom-right-radius: 5px;}#currentDurationLabel,#videoDurationLabel {color: #fff;font-size: 12px;font-weight: 700;}#videoTimeLine {background-color: transparent;border: 1px solid #4FD9FF;border-radius: 5px;height: 10px;}#videoTimeLine::chunk {background-color: #4FD9FF;border-radius: 4px;}#playBtn,#backwordBtn,#muteBtn,#forwordBtn,#fullScreenBtn,#repeatBtn {background-color: #FFD177;border: 0;border-radius: 8px;}#playBtn:hover,#backwordBtn:hover,#muteBtn:hover,#forwordBtn:hover,#fullScreenBtn:hover,#repeatBtn:hover {background-color: #FFC859;}#volumeSlider::groove {border-image: url(:/icon/img/volume_bg.png);}#volumeSlider::handle {border-image: url(:/icon/img/volume_handle.png);width: 19px;height: 19px;}#videoWidget {background: url(:/img/img/logo_large.png) #000 no-repeat center;}"
